mapper.select:
SELECT * FROM ( SELECT (ROW_NUMBER() OVER()) AS rn, lctrAll.* FROM ( SELECT l."LCTR_NUM", l."UNQ_NUM2", l."APLY_TYPE", l."APLY_YDM", l."PSCP_NOPE", l."LCTR_NAME", e."EMP_NM", e."EMP_TELNO" FROM public."LCTR" l LEFT OUTER JOIN public."EMP" e ON l."UNQ_NUM2" = e."UNQ_NUM" WHERE LENGTH(l."LCTR_NUM"::TEXT) >= 6 AND SUBSTR(l."LCTR_NUM"::TEXT, 6, 1) BETWEEN #{lectureType}::TEXT AND #{lectureTypeEnd}::TEXT
AND (l."APLY_TYPE" = #{aply_type}::INTEGER OR l."APLY_TYPE" = SUBSTR(#{aply_type}::TEXT, 2, 1)::INTEGER)
)lctrAll
"${search}"::TEXT LIKE '%'||#{keyword}||'%'
)
WHERE rn BETWEEN #{start} AND #{end}

mapper.insert:
		INSERT INTO 
			public."ATNDC_TYPE"(
				"SN",
				"LCTR_NUM",
				"CRTR_CNT"
				)
				(
				(SELECT COALESCE(MAX("SN"), 0) + 1 FROM public."ATNDC_TYPE"),
				#{lctr_num},
				#{crtr_cnt}
				)

mapper.select:
SELECT COUNT(*) FROM ( SELECT p."PST_NUM", p."UNQ_NUM", p."PST_TTL", p."PST_CLSF", p."WRT_YMD", p."DEL_YN", e."EMP_NM" as "empName", e."EMP_TELNO" as "empTelNo", s."STDNT_NM" as "studentName", s."STDNT_TELNO" as "studentTelNo" FROM public."PST" p LEFT OUTER JOIN public."EMP" e ON p."UNQ_NUM" = e."UNQ_NUM" LEFT OUTER JOIN public."STDNT" s ON p."UNQ_NUM" = s."UNQ_NUM" WHERE p."PST_CLSF" = #{ pst_clsf}
s."${search}"::TEXT LIKE '%'||#{keyword}||'%'
)

SELECT * FROM ( SELECT (ROW_NUMBER() OVER()) AS rn, pstAll.* FROM ( SELECT p."PST_NUM", p."UNQ_NUM", p."PST_TTL", p."PST_CLSF", p."WRT_YMD", p."DEL_YN", e."EMP_NM" as "empName", e."EMP_TELNO" as "empTelNo", s."STDNT_NM" as "studentName", s."STDNT_TELNO" as "studentTelNo" FROM public."PST" p LEFT OUTER JOIN public."EMP" e ON p."UNQ_NUM" = e."UNQ_NUM" LEFT OUTER JOIN public."STDNT" s ON p."UNQ_NUM" = s."UNQ_NUM" WHERE p."PST_CLSF" = #{ pst_clsf}
s."${search}"::TEXT LIKE '%'||#{keyword}||'%'
) pstAll )
WHERE rn BETWEEN #{start} AND #{end}

		SELECT "CONTENT"
		FROM public."SORT_CODE"
		WHERE "BCODE" 	= #{ bcode}
			AND "MCODE"	= #{ mcode}
